import React from "react";

// Komponent Notification służy do wyświetlania komunikatów na stronie
const Notification = (props) => {
  // Styl komunikatu
  const style = {
    background: "lightgrey",
    fontSize: "16px",
    borderStyle: "solid",
    borderRadius: "5px",
    padding: "10px",
    marginBottom: "10px",
  };

  // Styl dla komunikatu o błędzie
  const errorStyle = {
    color: "red",
  };

  // Styl dla komunikatu o sukcesie
  const successStyle = {
    color: "green",
  };

  return (
    // Komponent renderuje komunikat z odpowiednimi stylami w zależności od statusu
    <div
      style={
        props.status === "error"
          ? { ...style, ...errorStyle }
          : { ...style, ...successStyle }
      }
    >
      {props.message}
    </div>
  );
};

export default Notification;
